export const formValidator = (data) => {
  let errMsg = {};
  if (data.firstName.length === 0) {
    errMsg.firstName = 'first name should be non-empty';
  } else if (data.lastName.length === 0) {
    errMsg.firstName = '';
    errMsg.lastName = 'last name should be non-empty';
  } else if (data.password.length <= 3 && data.password.length >= 8) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = 'password length should be in the limit';
  } else if (data.c_password !== data.password) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = 'passwords must be equal';
  } else if (data.email.length !== 0) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = 'email should be valid';
  } else if (data.phone.length === 10) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = '';
    errMsg.phone = 'phone number should be valid';
  }else if (data.city.length !== 0) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = '';
    errMsg.phone = '';
    errMsg.city = 'city should be non-empty'
  }else if (data.country.length !== 0) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = '';
    errMsg.phone = '';
    errMsg.city = ''
    errMsg.country = 'country should be non-empty'
  }else if (data.age.length !== 0) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = '';
    errMsg.phone = '';
    errMsg.city = ''
    errMsg.country = ''
    errMsg.age = 'age should be non-empty'
  }
  else if (data.employeeId.length !== 0) {
    errMsg.firstName = '';
    errMsg.lastName = '';
    errMsg.password = '';
    errMsg.c_password = '';
    errMsg.email = '';
    errMsg.phone = '';
    errMsg.city = ''
    errMsg.country = ''
    errMsg.age = ''
    errMsg.employeeId = 'employeeId should be non-empty'
  }
};
